  const [userName, setOrderData] = useState([]);
    useEffect(() => {
        fetch('http://localhost:3004/order')
            .then(data => data.json())
            .then((data) => setOrderData(data))
    }, [])
  return(
    <>
    <header className="Head">
      <h1 className="head_title">LAUNDRY</h1>
      <ul type="none" className="lists">
        <li className="pricing">Pricing</li>
        <li className="career">career</li>
        <li className="user"><img src={Image} alt="avtar" height={50} width={50}/><div className="user_n">{data.user_name}</div></li>
      </ul>
      </header>
    </>
  )

}
export default Header;
