import React, { useState } from 'react'
import "./Signin.css"
const Signin = () => {
const [data,setdata]=useState({
  "User":"",
  "Password":""
})

  const handlesubmit=(e)=>{
e.preventDefault()
if(isNaN(data.User)===true){
  if(data.User.includes("@")){
    console.log("email")
  }else{
    console.log("Enter a valid email")
  }
}else{
  if((data.User+"").length===10){
    console.log("number")
  }else{
    console.log("Enter a valid number")
  }
}
console.log(data)
  }
  const handleinput=(e,id)=>{
setdata({...data,[id]:e.target.value})
  }
  return (
    <div id='Signin-comp'>
     <div id='Heading-signin'>Sign in</div>
     <form>
      <div className='signin-inputs-fields'>
      <div className='signin-inputs'>
      <label id='User-signin' placeholder='Mobile/Email'>Mobile/Email</label>
      <input required type="email/number" id="User" onChange={(e)=>handleinput(e,"User")}/>
      <div className='signin-blueline'></div>
      </div>
      <div className='signin-inputs'>
      <label id='Password-signin'placeholder='Password'>Password</label>
      <input required type="password" id='Password-s' onChange={(e)=>handleinput(e,"Password")} />
      <div className='signin-blueline'></div>
      </div>
      </div>
      <p className='Forgot-password'>Forgot Password?</p>
     <button onClick={(e)=>handlesubmit(e)} id="Button-signin">Sign In</button>
     <img id='Padlock' src='images/padlock.jpg'/>
     </form>
    
    

    </div>
  )
}

export default Signin
