import React, { useState } from 'react'
import "./Signin.css"
const Signin = () => {
const [passwordvalidity,setpasswordvalidity]=useState(false)
const [emaivalidity,setemailvalidity]=useState(false)
const [data,setdata]=useState({
  "User":"",
  "Password":""
})

  const handlesubmit=(e)=>{
e.preventDefault()
if(data.Password.length && data.User.length){
  console.log("done")
}else{
if(data.Password.length===0){
  setpasswordvalidity(true)
  console.log("enter a valid password")
}
if(data.User.length===0){
  setemailvalidity(true)
  console.log("enter a valid user")
}
}
  }
  const handleinput=(e,id)=>{
setdata({...data,[id]:e.target.value})
  }
  return (
    <div id='Signin-comp'>
     <div id='Heading-signin'>Sign in</div>
     <form>
      <div className='signin-inputs-fields'>
      <div className='signin-inputs'>
      <label id='User-signin' >Mobile/Email</label>
      <input placeholder='Mobile/Email' required type="email/number" id="User" onChange={(e)=>handleinput(e,"User")}/>
      {emaivalidity && <div className='signin-blueline'></div>}
      {!emaivalidity && 
      <>
      <p styles={{position:"absolute"}}>Enter a valid Phone//Number</p>
      <div className='signin-blueline' style={{backgroundColor:"red"}}></div></>}
      
      </div>
      <div className='signin-inputs'>
      <label id='Password-signin'>Password</label>
      <input placeholder='Password' required type="password" id='Password-s' onChange={(e)=>handleinput(e,"Password")} />
      <div className='signin-blueline'></div>
      </div>
      </div>
      <p className='Forgot-password'>Forgot Password?</p>
     <button onClick={(e)=>handlesubmit(e)} id="Button-signin">Sign In</button>
     <img id='Padlock' src='images/padlock.jpg'/>
     </form>
    
    

    </div>
  )
}

export default Signin
